    //     return ()=>{
    //         window.removeEventListener("resize", handleResize);
    //     };
    // }, []);

    return (
        <div 
            className={`node ${classes}`}
            id={`node-${rowIndex}-${colIndex}`}
            style={{width: window.innerWidth/sizeFactor, height: window.innerHeight/27.75}}></div>
    );
}

export default Node;
